import '../App.css';

import React, {Component} from 'react';


class TeamGenerator extends Component {
  constructor(props) {
    super();
    this.state = {blueTeam: [], redTeam: [], players: [], player: ''};
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.deletePlayer = this.deletePlayer.bind(this);
  }

  handleChange(event) {
    this.setState({player: event.target.value});
  }
  handleSubmit(event) {
    event.preventDefault();
    var ps = this.state.players, d = {},
        summs = this.props.data['summoners']['sorted_summoners'],
        p = this.state.player;
    for (var i in summs) {
      if (summs[i].toLowerCase() == this.state.player.toLowerCase()) {
        p = summs[i]
      }
    }
    ps.indexOf(this.state.player) === -1 ? ps.push(p) :
                                           console.log('duplicate value');

    d['players'] = ps;
    d['player'] = '';
    this.setState(d)
  }

  deletePlayer(e) {
    var summ = e.target.value, newState = this.state.players;
    for (var i in newState) {
      if (newState[i] == summ) {
        newState.splice(i, 1);
      }
    }
    this.setState({'players': newState})
  }
  render() {
    let d = this.props.data;

    if (typeof (d) == 'object') {
      d = d['summoners'];

      var playersCon = [];
      for (var i in this.state.players) {
        var winRate = '0.00%';
        if (d[this.state.players[i]]) {
          winRate = d[this.state.players[i]]['win rate']
        }
        playersCon.push(<div className = 'GplayerCont'>{parseInt(i) + 1}.) {
          this.state.players[i]
        } (Win rate: {winRate})<button onClick={this.deletePlayer} value={this.state.players[i]}className = 'GdeleteButton'>x</button></div>);
      }
    }

    return (
        <div className = 'dataContainer'><div className = 'GformInput'>
        <form onSubmit = {this.handleSubmit}><label>Add Summoner<br>
        </br>
        <input type = 'summonerName' onChange={
      this.handleChange} value={
      this.state.player}/>
        </label> <br></br><input type = 'submit' value = 'Add' />
        </form>
            </div>
        <div className = 'Gplayers'>{playersCon}</div></div>)
  }
}

export default TeamGenerator;
