import '../App.css';
import React, {Component} from 'react';

const importAll = require => require.keys().reduce((acc, next) => {
    acc[next.replace('./', '')] = require(next);
    return acc;
}, {});

const images =
    importAll(require.context('./images/champion_squares', false, /\.(png)$/));


class MatchHistory extends Component {
    constructor(props) {
        super();
    }

    render() {
        let d = this.props.data, matchElements = [],blueResult,redResult;
        let blueBans = [];
        let redBans = [];

        if(typeof (d) == 'object'){
            let matches = d['match_history'];
            for(var i in matches){
                console.log(matches[i])
                if(matches[i]['winning_team_color']=="blue"){
                    blueResult = "VICTORY";
                    redResult = "DEFEAT";
                }else if(matches[i]['winning_team_color']=="red"){
                    blueResult = "DEFEAT";
                    redResult = "VICTORY";
                } else{
                    blueResult = "UNDECIDED";
                    redResult = "UNDECIDED";
                }

                let matchResultsSection = <div className="resultsSection"><div className="blueResultsSection">{blueResult}</div><div className="redResultsSection">{redResult}</div></div>


                for(let p in matches[i]['blue_bans']){
                    var n = matches[i]['blue_bans'][p][0].toUpperCase() + matches[i]['blue_bans'][p][0].slice(1).toLowerCase() + ".png";

                    blueBans.push(<img className='bansChampImages' src={images[n]}></img>)

                }

                for(let p in matches[i]['bans']){
                    console.log(matches[i]['red_bans'][p])
                }


                let banSection = <div className="bans"><div className="blueBans">{blueBans}</div><div className="middleBans">Bans</div><div className="redBans"></div></div>


                let mc = <div className="matchContainer"><div className="matchHeader">{matches[i]['date']}</div>{matchResultsSection}{banSection}</div>

                matchElements.push(mc)
            }
        }
        return (<div className="dataContainer">{matchElements}</div>)
    }
}



export default MatchHistory;
